INSERT INTO `rice` (`id`, `ricename`, `categoryid`, `remarkable`, `general`, `area`, `price`, `img`) VALUES
(00002, 'กข43 (RD43)', 00001, 'อายุการเก็บเกี่ยวสั้น  95 วันเมื่อปลูกโดยวิธีหว่านน้ำตม\r\nคุณภาพของเมล็ดทางการหุงต้มรับประทาน ดี ข้าวสุกนุ่ม  มีกลิ่นหอมอ่อน\r\nค่อนข้างต้านทานต่อโรคไหม้และเพลี้ยกระโดดสีน้ำตาล\r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง สูงประมาณ 103 เซนติเมตร   \r\nอายุเก็บเกี่ยว 95 วัน\r\nทรงกอตั้ง ต้นค่อนข้างแข็ง ใบสีเขียวจาง ใบธงเอนปานกลาง  \r\nเมล็ดข้าวเปลือกสีฟาง ข้าวกล้องสีขาว  รูปร่างเรียว   \r\nระยะพักตัวของเมล็ดพันธุ์ประมาณ 5 สัปดาห์ \r\nผลผลิต ประมาณ 561 กิโลกรัมต่อไร่ \r\n', 'พื้นที่นาชลประทาน พื้นที่ที่มีน้ำท่วมขังเป็นเวลานานและเกษตรกรมีช่วงเวลาในการทำนาน้อยกว่าพื้นที่ปลูกข้าวอื่น ๆ และ/หรือพื้นที่ที่มีปัญหาข้าววัชพืชระบาด', 130, 'rice2.jpg'),
(00003, 'กข47 (RD47)', 00001, 'ผลผลิตสูง  มีเสถียรภาพดี\r\nค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลดีกว่า กข41 และค่อนข้างต้านโรคไหม้ดีกว่าพิษณุโลก 2\r\nคุณภาพเมล็ดทางกายภาพดีเป็นข้าวเจ้าเมล็ดยาวเรียว  ท้องไข่น้อย คุณภาพการสีดีถึงดีมาก  สามารถสีเป็นข้าวสาร 100 เปอร์เซ็นต์ได้\r\n', 'ไม่ไวต่อช่วงแสง\r\n อายุเก็บเกี่ยวประมาณ 90-100วัน (หว่านน้ำตม) และ 112 วัน(ปักดำ)\r\nลำต้นแข็งมาก ใบสีเขียว มุมใบธงกว้างปานกลาง รวงยาว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nสีขาวนวลไม่เลื่อมมัน ค่อนข้างร่วนและแข็ง\r\nผลผลิต ประมาณ 793 กิโลกรัมต่อไร่\r\n', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน ภาคเหนือตอนล่าง สำหรับเป็นทางเลือกของเกษตรกร    ในการป้องกันการแพร่ระบาดของเพลี้ยกระโดดสีน้ำตาล และโรคไหม้', 120, 'rice3.jpg'),
(00004, 'กข49 (RD49)', 00001, 'ผลผลิตสูง (ศักยภาพการให้ผลผลิตสูงถึง 939 กก./ไร่)สามารถผลิตเป็นข้าว 100 เปอร์เซ็นต์ ชั้น 1 ได้ค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลชีวชนิดใหม่ต้านทานโรคไหม้\r\n\r\n', 'ข้าวเจ้านาสวน ไม่ไวต่อช่วงแสง\r\nต้นสูงประมาณ 80-89 เซนติเมตร  \r\nอายุเก็บเกี่ยว 102-107 วัน (หว่านน้ำตม) \r\nทรงกอตั้ง ใบสีเขียวเข้ม ใบธงตั้ง รวงแน่นปานกลาง ระแง้ถี่ คอรวงสั้น ฟางแข็ง\r\nเมล็ดข้าวเปลือกสีฟาง\r\nผลผลิตเฉลี่ย 733 กก./ไร่\r\n', 'พื้นที่นาชลประทาน', 140, 'rice4.jpg'),
(00005, 'ชัยนาท 2 ', 00001, 'เป็นข้าวอายุสั้นกว่าพันธุ์ปทุมธานี 1 ประมาณ 7-9 วัน\r\nคุณภาพการหุงต้ม เป็นข้าวร่วน มีกลิ่นหอม เป็นทางเลือกของผู้บริโภคที่ชอบข้าวร่วนประเภทข้าวเสาไห้ แต่มีกลิ่นหอม\r\nต้านทานต่อเพลี้ยกระโดดสีน้ำตาลได้ดีกว่าพันธุ์ปทุมธานี 1\r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง สูงประมาณ 83 - 95 เซนติเมตร\r\nอายุเก็บเกี่ยวประมาณ 103 – 105 วัน\r\nทรงกอแบะ ใบสีเขียวเข้ม ใบธงเป็นแนวนอน รวงแน่นปานกลาง ระแง้ถี่ คอรวงยาว ต้นแข็ง ไม่ล้ม ใบค่อนข้างแก่เร็ว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nระยะพักตัวของเมล็ดพันธุ์ ประมาณ 5 สัปดาห์\r\nคุณภาพข้าวสุก ร่วน มีกลิ่นหอม\r\nผลผลิต ผลผลิตเฉลี่ย 657 กิโลกรัมต่อไร่\r\n', 'แนะนำให้ปลูกในพื้นที่การทำนาเขตชลประทาน ที่มีการทำนาอย่างต่อเนื่อง\r\nในภาคเหนือตอนล่าง พื้นที่ซึ่งน้ำท่วมเร็วในฤดูฝน มีช่วงระยะเวลาการทำนาค่อนข้างจำกัดเพื่อสามารถปลูกและเก็บเกี่ยวได้ทันเวลา\r\n', 120, 'rice5.jpg'),
(00006, 'ชัยนาท 80', 00002, '1. อายุสั้น มีอายุวันเก็บเกี่ยว 99 วัน ในฤดูนาปรัง และ103 วันในฤดูนาปี เมื่อปลูกโดยวิธีหว่านน้ำตม\r\n2. ผลผลิตสูง เฉลี่ย 876 กิโลกรัมต่อไร่\r\n3. ค่อนข้างต้านทานต่อเพลี้ยกระโดดสีน้ำตาลในภาคเหนือตอนล่าง และโรคขอบใบแห้ง\r\n4. คุณภาพการสีดีมาก สามารถสีเป็นข้าวขาว 100 เปอร์เซ็นต์\r\n5. มีปริมาณธาตุเหล็กในข้าวกล้อง 15.7 มิลลิกรัม ต่อ 1 กิโลกรัม ในข้าวสาร 6.7 มิลลิกรัมต่อ 1 กิโลกรัม\r\n', 'ชัยนาท 80 เป็นข้าวเจ้าไม่ไวต่อช่วงแสง อายุเก็บเกี่ยว 103 วัน ในฤดูนาปี และ 99 วันในฤดูนาปรัง เมื่อปลูกโดยวิธีหว่านน้ำตม กอตั้ง ต้นแข็งไม่ล้มง่าย สูงเฉลี่ย 104 เซนติเมตร ใบสีเขียวเข้ม ใบธงตั้งตรง รวงแน่นปานกลาง คอรวงยาว เปลือกเมล็ดสีฟาง ข้าวกล้องสีขาว เป็นท้องไข่น้อย รูปร่างเรียว ยาว 7.34 มิลลิเมตร กว้าง 2.23 มิลลิเมตร หนา 1.80 มิลลิเมตร มีปริมาณแอมิโลสสูง (26.6-29.4%) ระยะพักตัวของเมล็ด 4-6 สัปดาห์ ผลผลิต เฉลี่ย 876 กิโลกรัม/ไร่', 'พื้นที่นาชลประทาน ภาคเหนือตอนล่างและภาคกลางตอนบน ที่ต้องการข้าวอายุสั้น โดยเริ่มปลูกในเดือนสิงหาคม ธันวาคม และเมษายน หรือสำหรับปลูกหลังถูกน้ำท่วมในฤดูฝนและสามารถปลูกและเก็บเกี่ยวได้ 2 ครั้งในฤดูนาปรังก่อนถูกน้ำท่วม', 100, 'rice6.jpg'),
(00007, 'กข41 (RD41)', 00002, 'ผลผลิตสูง  มีเสถียรภาพดี ให้ผลผลิตเฉลี่ย 722 กก./ไร่   สูงกว่าสุพรรณบุรี 1  (645 กก./ไร่)  และชัยนาท 1 (640 กก./ไร่)  คิดเป็นร้อยละ 12  และ 13 ตามลำดับ แต่ไม่แตกต่างจากพิษณุโลก 2 (719 กก./ไร่)\r\nค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาล และโรคไหม้ \r\nคุณภาพเมล็ดทางกายภาพดีเป็นข้าวเจ้าเมล็ดยาวเรียว  ท้องไข่น้อย คุณภาพการสีดี  สามารถสีเป็นข้าวสาร 100 เปอร์เซ็นต์ได้ \r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง ความสูงประมาณ 104 เซนติเมตร   \r\nอายุเก็บเกี่ยว 105 วัน\r\nกอตั้ง ต้นแข็ง ใบและกาบใบสีเขียว ใบธงตั้งตรง คอรวงโผล่พ้นจากกาบใบธงเล็กน้อย ยอดเกสรตัวเมียสีขาว  \r\nเมล็ดข้าวเปลือกสีฟาง เปลือกเมล็ดมีขนสั้น  รูปร่างเรียว   \r\nคุณภาพการสีดีได้ข้าวเต็มเมล็ด\r\nระยะพักตัวของเมล็ดพันธุ์ประมาณ 9-10 สัปดาห์\r\nผลผลิต ประมาณ 722 กิโลกรัมต่อไร่่ \r\n', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน ภาคเหนือตอนล่าง สำหรับเป็นทางเลือกของเกษตรกรในการป้องกันการแพร่ระบาดของเพลี้ยกระโดดสีน้ำตาล   ', 120, 'rice7.jpg'),
(00008, 'สุพรรณบุรี 60', 00002, 'ผลผลิตสูง คุณภาพเมล็ดดี  คุณภาพการสีดี  ตอบสนองต่อปุ๋ยสูง  ต้านทานโรคใบสีส้ม และโรคไหม้  ต้านทานเพลี้ยกระโดดสีน้ำตาล เพลี้ยจักจั่นสีเขียว', 'เป็นข้าวเจ้า สูงประมาณ 133 เซนติเมตร  ไม่ไวต่อช่วงแสง  อายุเก็บเกี่ยว ประมาณ 120-122 วัน ใบสีเขียวเข้ม ทรงกอตั้ง รวงแน่น ระแง้ถี่ คอรวงสั้น เมล็ดรูปร่างเรียว ยาว ท้องไข่น้อย  เมล็ดข้าวเปลือกสีฟาง  ระยะพักตัวของเมล็ดประมาณ 4 สัปดาห์คุณภาพข้าวสุก ร่วน นุ่ม  ผลผลิต ประมาณ 700 กิโลกรัมต่อไร่', 'เขตชลประทานภาคกลาง ภาคตะวันตก และภาคตะวันออก', 100, 'rice26.jpg'),
(00009, 'ปทุมธานี 1', 00002, 'ผลผลิตสูง\r\nคุณภาพเมล็ดคล้ายพันธุ์ข้าวดอกมะลิ 105\r\nต้านทานเพลี้ยกระโดดสีน้ำตาล และเพลี้ยกระโดดหลังขาว\r\nต้านทานโรคไหม้ และโรคขอบใบแห้ง\r\n', 'เป็นข้าวเจ้า สูงประมาณ 104-133 เซนติเมตร\r\nไม่ไวต่อช่วงแสง\r\nอายุเก็บเกี่ยว ประมาณ 104-126 วัน\r\nทรงกอตั้ง ใบสีเขียวมีขน กาบใบและปล้องสีเขียว ใบธงยาว ทำมุม 45o กับคอรวง รวงอยู่ใต้ใบธง\r\nเมล็ดข้าวเปลือกสีฟาง มีขน มีหางเล็กน้อย\r\nระยะพักตัวของเมล็ดประมาณ 3-4 สัปดาห์ \r\nผลผลิต ประมาณ 650-774 กิโลกรัมต่อไร่\r\n', 'เขตชลประทานในภาคกลาง', 120, 'rice8.jpg'),
(00010, 'พิษณุโลก 2', 00002, 'ผลผลิตสูง และมีเสถียรภาพในการให้ผลผลิต \r\nต้านทานเพลี้ยกระโดดสีน้ำตาล เพลี้ยกระโดดหลังขาว และเพลี้ยจักจั่นสีเขียว\r\nคุณภาพการสีดี ท้องไข่น้อย\r\n', 'เป็นข้าวเจ้า สูงประมาณ 114 เซนติเมตร\r\nไม่ไวต่อช่วงแสง	\r\nอายุเก็บเกี่ยว 119-121 วัน\r\nทรงกอตั้ง ใบสีเขียวเข้ม ใบธงตั้ง รวงแน่นปานกลาง ระแง้ค่อนข้างถี่ คอรวงสั้น ฟางแข็ง ใบแก่ช้า\r\nเมล็ดข้าวเปลือกสีฟาง\r\nระยะพักตัวของเมล็ดประมาณ 8 สัปดาห์\r\nคุณภาพข้าวสุก ร่วน แข็ง\r\nผลผลิต ประมาณ 807 กิโลกรัมต่อไร่\r\n', 'ทุกภาคในเขตชลประทาน', 130, 'rice10.jpg'),
(00011, 'สุพรรณบุรี 3 (Suphan Buri 3)', 00002, 'ต้านทานเพลี้ยกระโดดสีน้ำตาล ดีกว่าพันธุ์สุพรรณบุรี 1\r\nต้านทานโรคไหม้และโรคขอบใบแห้ง	\r\nให้ผลผลิตสูงใกล้เคียงกับพันธุ์สุพรรณบุรี 1\r\n', 'เป็นข้าวเจ้า สูงประมาณ 114 เซนติเมตร \r\nไม่ไวต่อช่วงแสง \r\nอายุเก็บเกี่ยว 115 - 120 วัน \r\nลักษณะทรงกอตั้ง ต้นแข็ง ใบสีเขียว ใบธงค่อนข้างตั้ง \r\nเมล็ดข้าวเปลือกสีฟาง \r\nระยะพักตัวของเมล็ดพันธุ์ ประมาณ 5 สัปดาห์\r\nคุณภาพข้าวสุก ร่วน แข็ง ประเภทข้าวเสาไห้\r\nผลผลิต:  ประมาณ 772 กิโลกรัมต่อไร่	\r\n', 'นาชลประทานภาคกลางที่ทำนาต่อเนื่อง และพื้นที่ที่มีปัญหาการระบาดของเพลี้ยกระโดดสีน้ำตาล', 140, 'rice22.jpg'),
(00012, 'หอมมะลิ 105', 00003, '1. เหมาะกับการบริโภค หุงแล้วข้าวสุกมีกลิ่นหอม และอ่อนนุ่ม  2. ทนแล้งได้ดีพอสมควร  3. เมล็ดข้าวสารใส แกร่ง คุณภาพการสีดี  4. ทนต่อสภาพดินเปรี้ยว และดินเค็ม', 'เป็นข้าวเจ้าชนิดไวต่อช่วงแสง ต้นสูงเฉลี่ย 140 เซนติเมตร กอตั้ง ต้นข้าวอ่อนล้มง่าย รวงข้าวค่อนข้างยาวแน่น คอรวงยาว ระแง้ถี่ ก้านรวงอ่อน เป็นข้าวเจ้าที่มีคุณภาพเมล็ดดีมาก เมล็ดข้าวสารใส แข็งแรง ทนต่อสภาพดินเปรี้ยวและดินเค็ม มีความทนแล้ง ได้ดีพอสมควร ปลูกในพื้นที่ดอน สภาพข้าวไร่ได้ ผลผลิตประมาณ 363 กิโลกรัมต่อไร่', 'ปลูกได้ทั่วทุกภาคของประเทศไทย ในพื้นที่นาน้ำฝนที่มีระดับน้ำไม่เกิน 80 เซนติเมตร ในฤดูนาปี แต่คุณภาพข้าวสุกจะนุ่มและมีกลิ่นหอมมากที่สุดเมื่อปลูกในภาคตะวันออกเฉียงเหนือ', 170, 'rice12.jpg'),
(00013, 'ชัยนาท 1 ', 00003, 'ผลผลิตสูงตอบสนองต่อการใช้ปุ๋ยไนโตรเจนดี ต้านทานโรคใบหงิก และโรคไหม้  ต้านทานเพลี้ยกระโดดสีน้ำตาล และเพลี้ยกระโดดหลังขาว มีท้องไข่น้อย', 'เป็นข้าวเจ้า สูงประมาณ 113 เซนติเมตร ไม่ไวต่อช่วงแสง	อายุเก็บเกี่ยว ประมาณ 121-130 วัน ทรงกอตั้ง ใบสีเขียว ใบธงค่อนข้างยาวตั้งตรง คอรวงสั้น รวงยาวและแน่น ระแง้ค่อนข้างถี่ ฟางแข็ง  เมล็ดข้าวเปลือกสีฟาง	ระยะพักตัวของเมล็ดประมาณ 8 สัปดาห์  คุณภาพข้าวสุก ร่วน แข็ง  ผลผลิต ประมาณ 740 กิโลกรัมต่อไร่', 'ทุกภาคในเขตชลประทาน', 130, 'rice13.jpg'),
(00014, 'สุพรรณบุรี 1 (Suphan Buri 1)', 00003, 'ผลผลิตสูง \r\nตอบสนองต่อการใช้ปุ๋ย\r\nต้านทานโรคไหม้ โรคขอบใบแห้ง และต้านทานโรคใบหงิก และโรคใบสีส้ม ในสภาพธรรมชาติ\r\nต้านทานเพลี้ยกระโดดสีน้ำตาล และเพลี้ยกระโดดหลังขาว\r\n', 'เป็นข้าวเจ้านาสวน สูงประมาณ 125 เซนติเมตร\r\nไม่ไวต่อช่วงแสง\r\nอายุเก็บเกี่ยว ประมาณ 120 วัน\r\nทรงกอตั้ง ต้นแข็งไม่ล้ม ใบสีเขียวเข้ม มีขน กาบใบและปล้องสีเขียว ใบธงยาวค่อนข้างตั้งตรง คอรวงยาว รวงค่อนข้างแน่น\r\nเมล็ดข้าวเปลือกสีฟาง	\r\nระยะพักตัวของเมล็ดประมาณ 22 วัน\r\nคุณภาพข้าวสุก ร่วน แข็ง\r\nผลผลิต ประมาณ 806 กิโลกรัมต่อไร่ \r\n', 'ทุกภาคในเขตชลประทาน', 140, 'rice14.jpg'),
(00015, 'กข31 (ปทุมธานี 80)', 00003, 'คุณภาพเมล็ดทางกายภาพสม่ำเสมอกว่าพันธุ์สุพรรณบุรี 1\r\nต้านทานต่อเพลี้ยกระโดดหลังขาว ค่อนข้างต้านทานต่อเพลี้ยกระโดดสีน้ำตาล โรคขอบใบแห้ง โรคใบจุดสีน้ำตาล และโรคเมล็ดด่าง\r\nทรงกอตั้ง ต้นแข็ง ไม่ล้มง่าย ผลผลิตสูงกว่าผลผลิตของพันธุ์สุพรรณบุรี 1 ประมาณ 5 เปอร์เซ็นต์\r\n', 'ทรงกอตั้ง ต้นแข็งไม่ล้มง่าย ใบสีเขียว กาบใบสีเขียว ใบธงตั้ง คอรวงยาว    รวงยาว 29.9 เซนติเมตร\r\nเมล็ดข้าวเปลือกสีฟาง เมล็ดไม่มีหาง ข้าวกล้องสีขาว เป็นท้องไข่น้อย รูปร่างเรียว\r\nระยะพักตัวของเมล็ดพันธุ์ประมาณ 5 สัปดาห์\r\nอายุการเก็บเกี่ยว 107-125\r\nผลผลิตเฉลี่ย 745 กิโลกรัมต่อไร่ (ปักดำ) 738 กิโลกรัมต่อไร่ (นาหว่านน้ำตม)', 'นาชลประทานภาคกลาง', 130, 'rice15.jpg'),
(00016, 'กข5', 00001, '1. อายุสั้น มีอายุวันเก็บเกี่ยว 99 วัน ในฤดูนาปรัง และ103 วันในฤดูนาปี เมื่อปลูกโดยวิธีหว่านน้ำตม\r\n2. ผลผลิตสูง เฉลี่ย 876 กิโลกรัมต่อไร่\r\n3. ค่อนข้างต้านทานต่อเพลี้ยกระโดดสีน้ำตาลในภาคเหนือตอนล่าง และโรคขอบใบแห้ง\r\n4. คุณภาพการสีดีมาก สามารถสีเป็นข้าวขาว 100 เปอร์เซ็นต์\r\n5. มีปริมาณธาตุเหล็กในข้าวกล้อง 15.7 มิลลิกรัม ต่อ 1 กิโลกรัม ในข้าวสาร 6.7 มิลลิกรัมต่อ 1 กิโลกรัม\r\n', 'กข5  เป็นข้าวเจ้าไม่ไวต่อช่วงแสง อายุเก็บเกี่ยว 103 วัน ในฤดูนาปี และ 99 วันในฤดูนาปรัง เมื่อปลูกโดยวิธีหว่านน้ำตม กอตั้ง ต้นแข็งไม่ล้มง่าย สูงเฉลี่ย 104 เซนติเมตร ใบสีเขียวเข้ม ใบธงตั้งตรง รวงแน่นปานกลาง คอรวงยาว เปลือกเมล็ดสีฟาง ข้าวกล้องสีขาว เป็นท้องไข่น้อย รูปร่างเรียว ยาว 7.34 มิลลิเมตร กว้าง 2.23 มิลลิเมตร หนา 1.80 มิลลิเมตร มีปริมาณแอมิโลสสูง (26.6-29.4%) ระยะพักตัวของเมล็ด 4-6 สัปดาห์ ผลผลิต เฉลี่ย 876 กิโลกรัม/ไร่', 'พื้นที่นาชลประทาน ', 100, 'rice16.jpg'),
(00017, 'กข6', 00001, 'อายุการเก็บเกี่ยวสั้น  95 วันเมื่อปลูกโดยวิธีหว่านน้ำตม\r\nคุณภาพของเมล็ดทางการหุงต้มรับประทาน ดี ข้าวสุกนุ่ม  มีกลิ่นหอมอ่อน\r\nค่อนข้างต้านทานต่อโรคไหม้และเพลี้ยกระโดดสีน้ำตาล\r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง สูงประมาณ 103 เซนติเมตร   \r\nอายุเก็บเกี่ยว 95 วัน\r\nทรงกอตั้ง ต้นค่อนข้างแข็ง ใบสีเขียวจาง ใบธงเอนปานกลาง  \r\nเมล็ดข้าวเปลือกสีฟาง ข้าวกล้องสีขาว  รูปร่างเรียว   \r\nระยะพักตัวของเมล็ดพันธุ์ประมาณ 5 สัปดาห์ \r\nผลผลิตประมาณ 561 กิโลกรัมต่อไร่ \r\n', 'พื้นที่นาชลประทาน ', 120, 'rice17.jpg'),
(00018, 'กข8', 00001, 'ผลผลิตสูง  มีเสถียรภาพดี\r\nค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลดีกว่า กข41 และค่อนข้างต้านโรคไหม้ดีกว่าพิษณุโลก 2\r\nคุณภาพเมล็ดทางกายภาพดีเป็นข้าวเจ้าเมล็ดยาวเรียว  ท้องไข่น้อย คุณภาพการสีดีถึงดีมาก  สามารถสีเป็นข้าวสาร 100 เปอร์เซ็นต์ได้\r\n', 'ไม่ไวต่อช่วงแสง\r\nอายุเก็บเกี่ยวประมาณ 90-100วัน (หว่านน้ำตม) และ 112 วัน(ปักดำ)\r\nลำต้นแข็งมาก ใบสีเขียว มุมใบธงกว้างปานกลาง รวงยาว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nสีขาวนวลไม่เลื่อมมัน ค่อนข้างร่วนและแข็ง\r\nผลผลิต ประมาณ 793 กิโลกรัมต่อไร่\r\n', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน ', 130, 'rice18.jpg'),
(00019, 'กข12 (หนองคาย 80)', 00001, 'ผลผลิตสูง  มีเสถียรภาพดี\r\nค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลดีกว่า กข41 และค่อนข้างต้านโรคไหม้ดีกว่าพิษณุโลก 2\r\nคุณภาพเมล็ดทางกายภาพดีเป็นข้าวเจ้าเมล็ดยาวเรียว  ท้องไข่น้อย คุณภาพการสีดีถึงดีมาก  สามารถสีเป็นข้าวสาร 100 เปอร์เซ็นต์ได้\r\n', 'ไม่ไวต่อช่วงแสง\r\nอายุเก็บเกี่ยวประมาณ 90-100วัน (หว่านน้ำตม) และ 112 วัน(ปักดำ)\r\nลำต้นแข็งมาก ใบสีเขียว มุมใบธงกว้างปานกลาง รวงยาว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nสีขาวนวลไม่เลื่อมมัน ค่อนข้างร่วนและแข็ง\r\nผลผลิต ประมาณ 793 กิโลกรัมต่อไร่\r\n', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน', 100, 'rice19.jpg'),
(00020, 'กข13', 00001, 'เป็นข้าวอายุสั้นกว่าพันธุ์ปทุมธานี 1 ประมาณ 7-9 วัน\r\nคุณภาพการหุงต้ม เป็นข้าวร่วน มีกลิ่นหอม เป็นทางเลือกของผู้บริโภคที่ชอบข้าวร่วนประเภทข้าวเสาไห้ แต่มีกลิ่นหอม\r\nต้านทานต่อเพลี้ยกระโดดสีน้ำตาลได้ดีกว่าพันธุ์ปทุมธานี 1\r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง สูงประมาณ 83 - 95 เซนติเมตร\r\nอายุเก็บเกี่ยวประมาณ 103 – 105 วัน\r\nทรงกอแบะ ใบสีเขียวเข้ม ใบธงเป็นแนวนอน รวงแน่นปานกลาง ระแง้ถี่ คอรวงยาว ต้นแข็ง ไม่ล้ม ใบค่อนข้างแก่เร็ว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nระยะพักตัวของเมล็ดพันธุ์ ประมาณ 5 สัปดาห์\r\nคุณภาพข้าวสุก ร่วน มีกลิ่นหอม\r\nผลผลิต ผลผลิตเฉลี่ย 657 กิโลกรัมต่อไร่\r\n', 'แนะนำให้ปลูกในพื้นที่การทำนาเขตชลประทาน ที่มีการทำนาอย่างต่อเนื่อง\r\nในภาคเหนือตอนล่าง พื้นที่ซึ่งน้ำท่วมเร็วในฤดูฝน มีช่วงระยะเวลาการทำนาค่อนข้างจำกัดเพื่อสามารถปลูกและเก็บเกี่ยวได้ทันเวลา\r\n', 140, 'rice20.jpg'),
(00021, 'กข15', 00001, '1. อายุสั้น มีอายุวันเก็บเกี่ยว 99 วัน ในฤดูนาปรัง และ103 วันในฤดูนาปี เมื่อปลูกโดยวิธีหว่านน้ำตม\r\n2. ผลผลิตสูง เฉลี่ย 876 กิโลกรัมต่อไร่\r\n3. ค่อนข้างต้านทานต่อเพลี้ยกระโดดสีน้ำตาลในภาคเหนือตอนล่าง และโรคขอบใบแห้ง\r\n4. คุณภาพการสีดีมาก สามารถสีเป็นข้าวขาว 100 เปอร์เซ็นต์\r\n5. มีปริมาณธาตุเหล็กในข้าวกล้อง 15.7 มิลลิกรัม ต่อ 1 กิโลกรัม ในข้าวสาร 6.7 มิลลิกรัมต่อ 1 กิโลกรัม\r\n', 'กข15 เป็นข้าวเจ้าไม่ไวต่อช่วงแสง อายุเก็บเกี่ยว 103 วัน ในฤดูนาปี และ 99 วันในฤดูนาปรัง เมื่อปลูกโดยวิธีหว่านน้ำตม กอตั้ง ต้นแข็งไม่ล้มง่าย สูงเฉลี่ย 104 เซนติเมตร ใบสีเขียวเข้ม ใบธงตั้งตรง รวงแน่นปานกลาง คอรวงยาว เปลือกเมล็ดสีฟาง ข้าวกล้องสีขาว เป็นท้องไข่น้อย รูปร่างเรียว ยาว 7.34 มิลลิเมตร กว้าง 2.23 มิลลิเมตร หนา 1.80 มิลลิเมตร มีปริมาณแอมิโลสสูง (26.6-29.4%) ระยะพักตัวของเมล็ด 4-6 สัปดาห์ ผลผลิต เฉลี่ย 876 กิโลกรัม/ไร่', 'เขตชลประทาน ', 120, 'rice21.jpg'),
(00022, 'กข27', 00001, '1. อายุสั้น มีอายุวันเก็บเกี่ยว 99 วัน ในฤดูนาปรัง และ103 วันในฤดูนาปี เมื่อปลูกโดยวิธีหว่านน้ำตม\r\n2. ผลผลิตสูง เฉลี่ย 876 กิโลกรัมต่อไร่\r\n3. ค่อนข้างต้านทานต่อเพลี้ยกระโดดสีน้ำตาลในภาคเหนือตอนล่าง และโรคขอบใบแห้ง\r\n4. คุณภาพการสีดีมาก สามารถสีเป็นข้าวขาว 100 เปอร์เซ็นต์\r\n5. มีปริมาณธาตุเหล็กในข้าวกล้อง 15.7 มิลลิกรัม ต่อ 1 กิโลกรัม ในข้าวสาร 6.7 มิลลิกรัมต่อ 1 กิโลกรัม\r\n', 'กข27 เป็นข้าวเจ้าไม่ไวต่อช่วงแสง อายุเก็บเกี่ยว 103 วัน ในฤดูนาปี และ 99 วันในฤดูนาปรัง เมื่อปลูกโดยวิธีหว่านน้ำตม กอตั้ง ต้นแข็งไม่ล้มง่าย สูงเฉลี่ย 104 เซนติเมตร ใบสีเขียวเข้ม ใบธงตั้งตรง รวงแน่นปานกลาง คอรวงยาว เปลือกเมล็ดสีฟาง ข้าวกล้องสีขาว เป็นท้องไข่น้อย รูปร่างเรียว ยาว 7.34 มิลลิเมตร กว้าง 2.23 มิลลิเมตร หนา 1.80 มิลลิเมตร มีปริมาณแอมิโลสสูง (26.6-29.4%) ระยะพักตัวของเมล็ด 4-6 สัปดาห์ ผลผลิต เฉลี่ย 876 กิโลกรัม/ไร่', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน ภาคเหนือตอนล่าง ', 130, 'rice22.jpg'),
(00023, 'กข35', 00001, 'อายุการเก็บเกี่ยวสั้น  95 วันเมื่อปลูกโดยวิธีหว่านน้ำตม\r\nคุณภาพของเมล็ดทางการหุงต้มรับประทาน ดี ข้าวสุกนุ่ม  มีกลิ่นหอมอ่อน\r\nค่อนข้างต้านทานต่อโรคไหม้และเพลี้ยกระโดดสีน้ำตาล\r\n', 'เป็นข้าวเจ้าไม่ไวต่อช่วงแสง สูงประมาณ 103 เซนติเมตร   \r\nอายุเก็บเกี่ยว 95 วัน\r\nทรงกอตั้ง ต้นค่อนข้างแข็ง ใบสีเขียวจาง ใบธงเอนปานกลาง  \r\nเมล็ดข้าวเปลือกสีฟาง ข้าวกล้องสีขาว  รูปร่างเรียว   \r\nระยะพักตัวของเมล็ดพันธุ์ประมาณ 5 สัปดาห์ \r\nผลผลิตประมาณ 561 กิโลกรัมต่อไร่ \r\n', 'พื้นที่นาชลประทาน', 130, 'rice23.jpg'),
(00024, 'กำผาย 15', 00001, 'ผลผลิตสูง  มีเสถียรภาพดี\r\nค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลดีกว่า กข41 และค่อนข้างต้านโรคไหม้ดีกว่าพิษณุโลก 2\r\nคุณภาพเมล็ดทางกายภาพดีเป็นข้าวเจ้าเมล็ดยาวเรียว  ท้องไข่น้อย คุณภาพการสีดีถึงดีมาก  สามารถสีเป็นข้าวสาร 100 เปอร์เซ็นต์ได้\r\n', 'ไม่ไวต่อช่วงแสง\r\nอายุเก็บเกี่ยวประมาณ 90-100วัน (หว่านน้ำตม) และ 112 วัน(ปักดำ)\r\nลำต้นแข็งมาก ใบสีเขียว มุมใบธงกว้างปานกลาง รวงยาว\r\nเมล็ดข้าวเปลือกสีฟาง\r\nสีขาวนวลไม่เลื่อมมัน ค่อนข้างร่วนและแข็ง\r\nผลผลิต ประมาณ 793 กิโลกรัมต่อไร่\r\n', 'เหมาะสำหรับปลูกในพื้นที่นาชลประทาน ภาคเหนือตอนล่าง ', 100, 'rice24.jpg'),
(00025, 'เก้ารวง 88', 00001, 'ผลผลิตสูง (ศักยภาพการให้ผลผลิตสูงถึง 939 กก./ไร่)สามารถผลิตเป็นข้าว 100 เปอร์เซ็นต์ ชั้น 1 ได้ค่อนข้างต้านทานเพลี้ยกระโดดสีน้ำตาลชีวชนิดใหม่ต้านทานโรคไหม้\r\n\r\n', 'ข้าวเจ้านาสวน ไม่ไวต่อช่วงแสง\r\nต้นสูงประมาณ 80-89 เซนติเมตร  \r\nอายุเก็บเกี่ยว 102-107 วัน (หว่านน้ำตม) \r\nทรงกอตั้ง ใบสีเขียวเข้ม ใบธงตั้ง รวงแน่นปานกลาง ระแง้ถี่ คอรวงสั้น ฟางแข็ง\r\nเมล็ดข้าวเปลือกสีฟาง\r\nผลผลิตเฉลี่ย 733 กก./ไร่\r\n', 'พื้นที่นาชลประทาน', 120, 'rice25.jpg'),
